// https://leetcode.com/problems/minimum-window-substring/
class Solution {
  /* ---------------------------------------------------------------------------- */
  String minWindow(String s, String t) {
    if (t == s) return s;
    if (s.isEmpty || t.isEmpty || s.length < t.length ) return '';

    var idxs = <int>[];
    // buffer is S purged of chars no needed
    var buffer = StringBuffer();
    // Create counter for S
    var counterS = <String, int>{};
    for (var i = 0; i < s.length; i++) {
      var ch = s[i];
      if (t.contains(ch)) {
        idxs.add(i);
        buffer.write(ch);
        counterS[ch] = (counterS[ch] ?? 0) + 1;
      }
    }
    // Create counter for T
    var counterT = <String, int>{};
    for (var i = 0; i < t.length; i++) {
      var ch = t[i];
      counterT[ch] = (counterT[ch] ?? 0) + 1;
    }
    
    // ignore: prefer_function_declarations_over_variables
    final containsChars = () => counterT.keys.every((k) => counterS[k] != null && counterS[k]! >= counterT[k]!);
    // Check if S contains every character (and its frequency) of T
    if (!containsChars()) return '';
    counterS.updateAll((key, value) => 0);
    
    var target = buffer.toString();
    var start = 0;
    var minLen = -1;
    var minSStr = <int>[];
    var lastChar = '';
    for (var i = 0; i < target.length; i++) {
      var ch = target[i];
      counterS[ch] = counterS[ch]! + 1;
      if (i+1 >= t.length) {
        var found = lastChar.isNotEmpty ? lastChar == ch : containsChars();
        while (found) {
          var curLen = idxs[i] - idxs[start] + 1;
          if (minLen < 0 || curLen < minLen) {
            minLen = curLen;
            minSStr = [idxs[start], idxs[i]];
          }
          lastChar = target[start];
          counterS[lastChar] = counterS[lastChar]! - 1;
          found = counterS[lastChar]! >= counterT[lastChar]!;
          start++;
        }
      }
    }

    return minSStr.isNotEmpty 
      ? s.substring(minSStr.first, minSStr.last+1) 
      : '';
  }
  /* ---------------------------------------------------------------------------- */
  void minWindow2(String s, String t) {
    print(minWindow(s, t));
    print('-'*30);
  }
}

void main(List<String> args) {
  Solution()
  ..minWindow2('ADOBECODEBANC', 'ABC')    // BANC
  ..minWindow2('AmmBmmmCmmBBmAmmmmC', 'ABC')  // CmmBBmA
  ..minWindow2('ab', 'a')   // a
  ..minWindow2('abc', 'ac')   // abc
  ..minWindow2('abc', 'cba')   // abc
  ..minWindow2('abbbb', 'aa')   // ''
  // ..minWindow2('', '')   // twx...xlw
  ;
}
